import { signOut, onAuthStateChanged } from "firebase/auth";
import { auth, db } from "../../firebase.js";
import { useNavigate } from "react-router-dom";
import React, { useEffect, useState } from "react";
import { useSnackbar } from "notistack";
import { uid } from "uid";
import { set, ref, onValue, remove, update } from "firebase/database";
import Toolbar from "@mui/material/Toolbar";
import IconButton from "@mui/material/IconButton";
import AddIcon from "@mui/icons-material/Add";
import AppBar from "@mui/material/AppBar";
import { styled } from "@mui/material/styles";
import Fab from "@mui/material/Fab";
import Box from "@mui/material/Box";
import HomeIcon from "@mui/icons-material/Home";
const StyledFabHome = styled(Fab)({
  position: "absolute",
  zIndex: 1,
  top: -30,
  left: 30,
  right: 0,
  margin: "0 auto",
  border: "4px solid white",
});
const StyledFabAdd = styled(Fab)({
  position: "absolute",
  zIndex: 1,
  top: '50%',
  translate: ' 50px',
  left: 0,
  right: 0,
  margin: "0 auto",
  border: "4px solid white",
});
const StyledFabSetting = styled(Fab)({
  position: "absolute",
  zIndex: 1,
  top: '-50%',
  left: 0,
  right: 30,
  margin: "0 auto",
  border: "4px solid white",
});

function Home() {
  const navigate = useNavigate();
  const { enqueueSnackbar } = useSnackbar();
  const [todo, setTodo] = useState("");
  const [time, setTime] = useState("");
  const [todos, setTodos] = useState([]);
  const [isEdit, setIsEdit] = useState(false);
  const [tempUidd, setTempUidd] = useState("");
  const [complete, setComplete] = useState(0);

  useEffect(() => {
    auth.onAuthStateChanged((user) => {
      if (user) {
        onValue(ref(db, `/${auth.currentUser.uid}`), (snapshot) => {
          setTodos([]);
          const data = snapshot.val();
          if (data !== null) {
            Object.values(data).map((todo) => {
              setTodos((oldArray) => [...oldArray, todo]);
            });
          }
        });
      }
      if (!user) {
        navigate("/");
      }
    });
  }, []);
  const handleSignOut = () => {
    signOut(auth)
      .then(() => {
        navigate("/");
        const variant = "success";
        enqueueSnackbar("Logout success! ", { variant });
      })
      .catch((err) => {
        alert("not");
      });
  };

  const writeToDatabase = () => {
    const uidd = uid(); //sadawqeqw
    set(ref(db, `${auth.currentUser.uid}/${uidd}`), {
      todo: todo,
      time: time,
      uidd: uidd,
    });
    setTime("");
    setTodo("");
  };

  const handleDelete = (uid) => {
    remove(ref(db, `${auth.currentUser.uid}/${uid}`));
  };

  const handleUpdate = (todo) => {
    setIsEdit(true);
    setTodo(todo.todo);
    setTempUidd(todo.uidd);
  };

  const handleEditConfirm = () => {
    update(ref(db, `${auth.currentUser.uid}/${tempUidd}`), {
      todo: todo,
      tempUidd: tempUidd,
    });
    setTodo("");
    setIsEdit(false);
  };

  return (
    <div>
      <h1>Todo-List App</h1>
      <h1>Home</h1>
      <input
        type="text"
        placeholder="Add todo..."
        value={todo}
        onChange={(e) => {
          setTodo(e.target.value);
        }}
      />
      <input
        type="text"
        placeholder="Add todo..."
        value={time}
        onChange={(e) => {
          setTime(e.target.value);
        }}
      />
      {todos.map((todo, index) => (
        <div key={index} onClick={() => setComplete(1)}>
          <h5>{todo.todo}</h5>
          <button onClick={() => handleUpdate(todo)}>Update</button>
          <button onClick={() => handleDelete(todo.uidd)}>Delete</button>
        </div>
      ))}
      <div style={{ backgroundColor: "red" }}>
        {" "}
        {todos
          .filter((item) => item.time === 2)
          .map((todo, index) => (
            <div key={index}>
              <h5>{todo.todo}</h5>
              <button onClick={() => handleUpdate(todo)}>Update</button>
              <button onClick={() => handleDelete(todo.uidd)}>Delete</button>
            </div>
          ))}
      </div>
      {isEdit ? (
        <div>
          <button onClick={handleEditConfirm}>Confirm</button>
        </div>
      ) : (
        <div>
          {" "}
          <button onClick={writeToDatabase}>Add</button>
        </div>
      )}
      <button onClick={handleSignOut}>SignOut</button>
      <Box position='fixed' sx={{ height: '80px',left: 0,right: 0 , bottom: 0, backgroundColor: 'red' }}>
          <StyledFabHome color="secondary" aria-label="add">
            <AddIcon />
          </StyledFabHome>
          <StyledFabAdd color="secondary" aria-label="add">
            <AddIcon />
          </StyledFabAdd>
          <StyledFabSetting color="secondary" aria-label="add">
            <AddIcon />
          </StyledFabSetting>
      </Box>
    </div>
  );
}

export default Home;
