import {
  signInWithEmailAndPassword,
  onAuthStateChanged,
  createUserWithEmailAndPassword,
  signInWithPopup
} from "firebase/auth";
import React, { useState, useEffect } from "react";
import { auth } from "../../firebase.js";
import { useNavigate } from "react-router-dom";
import { useSnackbar } from "notistack";
import "./welcome.css";
import EmailIcon from "@mui/icons-material/Email";
import GoogleIcon from "@mui/icons-material/Google";
import Modal from '@mui/material/Modal';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';

const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: '80%',
    bgcolor: 'background.paper',
    border: '2px solid #000',
    boxShadow: 24,
  };

function Welcome() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isRegister, setIsRegister] = useState(false);
  const [registerInfo, setRegisterInfo] = useState({
    email: "",
    password: "",
    confirmPassword: "",
  });
  const { enqueueSnackbar } = useSnackbar();
  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const navigate = useNavigate();

  useEffect(() => {
    auth.onAuthStateChanged((user) => {
      if (user) {
        navigate("/home");
      }
    });
  }, []);

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSignInGG = () => {

  }

  const handleSignIn = () => {
    signInWithEmailAndPassword(auth, email, password)
      .then(() => {
        navigate("/home");
        const variant = "success";
        enqueueSnackbar("Login success! ", { variant });
      })
      .catch((err) => {
        const variant = "error";
        enqueueSnackbar("Wrong email or password! ", { variant });
      });
  };

  const handleRegister = () => {
    if (registerInfo.password !== registerInfo.confirmPassword) {
      const variant = "warning";
      enqueueSnackbar("Please confirm that password the same!", { variant });
      return;
    }

    createUserWithEmailAndPassword(
      auth,
      registerInfo.email,
      registerInfo.password
    )
      .then(() => {
        navigate("/");
        const variant = "success";
        enqueueSnackbar("Register success! ", { variant });
        setIsRegister(false);
      })
      .catch((err) => alert("Sai mk r tk ngu"));
  };

  return (
    <div className="wrapper">
      <div className="login">
        <button className="btn-login" onClick={handleOpen}>
          <EmailIcon sx={{color: 'red'}} />
          <p>Continue with email</p>
        </button>
        <button className="btn-login">
          <GoogleIcon sx={{color: 'yellow'}} />
          <p>Continue with Google</p>
        </button>
      </div>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
        <div className="login-register-container">
        {isRegister ? (
          <div className="form-register">
            <input
              placeholder="Email"
              type="text"
              value={registerInfo.email}
              onChange={(e) =>
                setRegisterInfo({ ...registerInfo, email: e.target.value })
              }
            />
            <input
              placeholder="Password"
              type="password"
              value={registerInfo.password}
              onChange={(e) =>
                setRegisterInfo({ ...registerInfo, password: e.target.value })
              }
            />
            <input
              placeholder="Confirm Password"
              type="password"
              value={registerInfo.confirmPassword}
              onChange={(e) =>
                setRegisterInfo({
                  ...registerInfo,
                  confirmPassword: e.target.value,
                })
              }
            />
            <button onClick={handleRegister}>Register</button>
            <button onClick={() => setIsRegister(false)}>Go back</button>
          </div>
        ) : (
          <div className="form-login">
            <input
              placeholder="Account"
              type="text"
              onChange={handleEmailChange}
              value={email}
            />
            <input
              type="password"
              placeholder="Password"
              onChange={handlePasswordChange}
              value={password}
            />
            <button onClick={handleSignIn}>Sigin</button>
            <button onClick={() => setIsRegister(true)}>Create account</button>
          </div>
        )}
      </div>
        </Box>
      </Modal>
    </div>
  );
}

export default Welcome;
