import { signOut, onAuthStateChanged } from "firebase/auth";
import { auth, db } from "../../firebase.js";
import { useNavigate } from "react-router-dom";
import React, { useEffect, useState } from "react";
import { useSnackbar } from "notistack";
import { uid } from "uid";
import { set, ref, onValue, remove, update } from "firebase/database";
import IconButton from "@mui/material/IconButton";
import AddIcon from "@mui/icons-material/Add";
import Fab from "@mui/material/Fab";
import Box from "@mui/material/Box";
import HomeIcon from "@mui/icons-material/Home";
import SettingsIcon from "@mui/icons-material/Settings";
import BrushIcon from "@mui/icons-material/Brush";
import Checkbox from "@mui/material/Checkbox";
import FastfoodIcon from '@mui/icons-material/Fastfood';
import WorkIcon from '@mui/icons-material/Work';
import FitnessCenterIcon from '@mui/icons-material/FitnessCenter';
import AccessAlarmIcon from '@mui/icons-material/AccessAlarm';
import "./Home.css";

function Home() {
  const navigate = useNavigate();
  const { enqueueSnackbar } = useSnackbar();
  const [todo, setTodo] = useState("");
  const [time, setTime] = useState("");
  const [todos, setTodos] = useState([]);
  const [isEdit, setIsEdit] = useState(false);
  const [tempUidd, setTempUidd] = useState("");
  const [complete, setComplete] = useState(0);

  useEffect(() => {
    auth.onAuthStateChanged((user) => {
      if (user) {
        onValue(ref(db, `/${auth.currentUser.uid}`), (snapshot) => {
          setTodos([]);
          const data = snapshot.val();
          if (data !== null) {
            Object.values(data).map((todo) => {
              setTodos((oldArray) => [...oldArray, todo]);
            });
          }
        });
      }
      if (!user) {
        navigate("/");
      }
    });
  }, []);
  const handleSignOut = () => {
    signOut(auth)
      .then(() => {
        navigate("/");
        const variant = "success";
        enqueueSnackbar("Logout success! ", { variant });
      })
      .catch((err) => {
        alert("not");
      });
  };

  const writeToDatabase = () => {
    const uidd = uid(); //sadawqeqw
    set(ref(db, `${auth.currentUser.uid}/${uidd}`), {
      todo: todo,
      time: time,
      uidd: uidd,
    });
    setTime("");
    setTodo("");
  };

  const handleDelete = (uid) => {
    remove(ref(db, `${auth.currentUser.uid}/${uid}`));
  };

  const handleUpdate = (todo) => {
    setIsEdit(true);
    setTodo(todo.todo);
    setTempUidd(todo.uidd);
  };

  const handleEditConfirm = () => {
    update(ref(db, `${auth.currentUser.uid}/${tempUidd}`), {
      todo: todo,
      tempUidd: tempUidd,
    });
    setTodo("");
    setIsEdit(false);
  };

  // {todos.map((todo, index) => (
  //   <div className="item-todo" key={index} onClick={() => setComplete(1)}>
  //     <div className="card-todo">
  //       <BrushIcon />
  //       <button onClick={() => handleUpdate(todo)}>Update</button>
  //       <button onClick={() => handleDelete(todo.uidd)}>Delete</button>
  //     </div>
  //   </div>
  // ))}

  return (
    <div className="wrapper-home">
      <div className="list-todo">
        <h2 className="title">Today's schedule</h2>
        <h2 className="date">Monday 19</h2>
        {todos.map((todo, index) => (
          <div className='item-todo' key={index} onClick={() => setComplete(1)}>
            <Checkbox color="success" />
            <div className={complete=== 0 ? 'card-todo' : 'card-todo complete'}>
              {(todo.category === "design" && (
                <button className="design-icon">
                  <BrushIcon />
                </button>
              )) ||
                (todo.category === "food" && (
                  <button className="food-icon">
                    <FastfoodIcon />
                  </button>
                ))
                ||
                (todo.category === "work" && (
                  <button className="work-icon">
                    <WorkIcon />
                  </button>
                ))
                ||
                (todo.category === "workout" && (
                  <button className="workout-icon">
                    <FitnessCenterIcon />
                  </button>
                ))
                ||
                (todo.category === "alarm" && (
                  <button className="alarm-icon">
                    <AccessAlarmIcon />
                  </button>
                ))
                ||
                ((
                  <button className="design-icon">
                    <WorkIcon />
                  </button>
                ))
              }
              <h4 className="title-todo">{todo.todo}</h4>
              <p className="time">7:00 AM</p>
            </div>
          </div>
        ))}
      </div>
      <h1>Todo-List App</h1>
      <h1>Home</h1>
      <input
        type="text"
        placeholder="Add todo..."
        value={todo}
        onChange={(e) => {
          setTodo(e.target.value);
        }}
      />
      <input
        type="text"
        placeholder="Add todo..."
        value={time}
        onChange={(e) => {
          setTime(e.target.value);
        }}
      />
      <div style={{ backgroundColor: "red" }}>
        {" "}
        {todos
          .filter((item) => item.time === 2)
          .map((todo, index) => (
            <div key={index}>
              <h5>{todo.todo}</h5>
              <button onClick={() => handleUpdate(todo)}>Update</button>
              <button onClick={() => handleDelete(todo.uidd)}>Delete</button>
            </div>
          ))}
      </div>
      {isEdit ? (
        <div>
          <button onClick={handleEditConfirm}>Confirm</button>
        </div>
      ) : (
        <div>
          {" "}
          <button onClick={writeToDatabase}>Add</button>
        </div>
      )}
      <button onClick={handleSignOut}>SignOut</button>
      <div className="nav-bar-bottom">
        <button className="btn-home">
          <HomeIcon />
        </button>
        <button className="btn-add">
          <AddIcon />
        </button>
        <button className="btn-setting">
          <SettingsIcon />
        </button>
      </div>
    </div>
  );
}

export default Home;
